import React, { Component } from 'react';
import './App.css';
import AddPerson from '../AddPerson/AddPerson'
import AddInfo from '../AddInfo/AddInfo'
import { HashRouter as Router, Link, Route } from 'react-router-dom'

class App extends Component {

  state = {
    response: '',
    postPerson: {
      biblical_name: '',
      nickname: ''
    },
    responseToPost: ''
  };

  componentDidMount() {
    this.getAllPeople()
      .then(res => this.setState({ response: res }))
      .catch(err => console.log(err));
  }

  getAllPeople = async () => {
    const response = await fetch('/api/people');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getOnePerson = async () => {
    const response = await fetch('/api/onePerson')
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  }

  handleSubmitPerson = async event => {
    event.preventDefault();
    const response = await fetch('/api/addPerson', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ postPerson: this.state.postPerson }),
    });
    const body = await response.text();
    this.setState({ responseToPost: body });
  }

  handleChangeFor = (property) => {
    return (event) => {
        this.setState({
            postPerson: {
                ...this.state.postPerson,
                [property]: event.target.value,
            }
    })
}
}

render() {
    return (
      <Router>
      <div className="App">
        <header className="App-header">
        <Link className = "link-options" to = '/peopleOfPromise'>People of Promise</Link>
        <Link className = "link-options" to = '/lesson'>Lesson</Link>
        <Link className = "link-options" to = '/study'>Study</Link>
        <Link className = "link-options" to = '/maps'>Maps</Link>
        <strong className = "link-options" >People Of Promise</strong>
        <Link className = "link-options" to = '/timeline'>Timeline</Link>
        <Link className = "link-options" to = '/gallery'>Gallery</Link>
        <Link className = "link-options" to = '/youth'>Youth</Link>
        <Link className = "link-options" to = '/addPerson'>Admin Page</Link>
        </header>
        <Route 
        path = '/addPerson' render = {() => (<AddPerson post = {this.state.postPerson} responseToPost = {this.state.responseToPost} handleChangeFor= {this.handleChangeFor}/>)}
        />
        <Route 
        path = '/addInfo' render = {() => (<AddInfo handleSubmit = {this.handleSubmitPerson} name = {this.state.postPerson.biblical_name}/>)}
        />
      </div>
      </Router>
    );
  }
}

export default App;
