import React, { Component } from 'react';
import './AddInfo.css';

class AddInfo extends Component {

  componentWillMount() { 
    this.getBooks();
  }

  state = {
    lesson: '',
    question: '',
    mapUrl: '',
    timelineUrl: '',
    galleryUrl: '',
    books: '',
    responseBooks: '',
    bibleBookId: '',
    bibleBookName: '',
    bibleChapter: '',
    bibleVerse1: '',
    bibleVerse2: '',
    chapters: ''
  }
getBooks = async () => {
  const response = await fetch('/api/getBooks')
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  const items = (body.map(book => <option key = {book.bible_book_id} value = {book.bible_book_id}>{book.bible_book_name}</option>));
  this.setState({ books: items})
  this.setState({ responseBooks: body})
  console.log(this.state.responseBooks)
}


getVerse = async () => {
  console.log('hello')
  const bibleId = 'f72b840c855f362c-04'
  const response = await fetch(`https://api.scripture.api.bible/v1/bibles/${bibleId}/passages/${this.state.bibleBookId}.${this.state.bibleChapter}.${this.state.bibleVerse1}-${this.state.bibleBookId}.${this.state.bibleChapter}.${this.state.bibleVerse2}?content-type=text`, {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'api-key': '230adc9049bc15384c218ba474b48eca'
    },
  })
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  this.setState({lesson: body.data.content})
  console.log(body.data.content)
}

handleChangeFor = (property) => {
  return (event) => {
      this.setState({
              [property]: event.target.value,
  })
}
}

select = (event) => {
  let bookChapters;
  const bibleId = event.currentTarget.value
  this.setState({
    bibleBookId: bibleId,
    bibleBookName: event.currentTarget.textContent
  }, () =>{
    const books = (this.state.responseBooks)
for (let book of books) {
  if(book.bible_book_id === bibleId) {
    bookChapters = book.chapters
  }
}

let chapterArray = []
let counter = 1

while(bookChapters >= counter) {
  chapterArray.push(counter)
  counter++;
}

console.log('chapterArray', chapterArray)

const chapterOptions = (chapterArray.map(chapter => <option onChange= {this.chapterOption} key = {chapter}>{chapter}</option>));
this.setState({chapterOptions: chapterOptions})
  })
}

chapterOption = (event) => {
  console.log(event.currentTarget.value)
  this.setState({bibleChapter: event.currentTarget.value})
}




render() {
    return (
        <div>
        <form onSubmit={this.props.handleSubmit}>
          <p>
            <strong>Add Information for {this.props.name}</strong>
          </p>
          <label>
          Choose Book: 
          <select onChange ={this.select}> {this.state.books}</select>
          </label>
          <label>
          Choose Chapter: 
          <select onChange = {this.chapterOption}> {this.state.chapterOptions}</select>
          </label>
          <input
            type="text"
            placeholder = "Verse 1"
            value={this.state.bibleVerse1}
            onChange= {this.handleChangeFor('bibleVerse1')}
          />
          <input
            type="text"
            placeholder = "Verse 2"
            value={this.state.bibleVerse2}
            onChange= {this.handleChangeFor('bibleVerse2')}
          />
          <button onClick= {this.getVerse}>Submit</button>
          <div className="display-linebreak">
          Lesson to add for {this.props.name}: {this.state.lesson}
          </div>
          <input
            type="text"
            placeholder = "Add Question"
            value={this.state.question}
            onChange= {this.handleChangeFor('question')}
          />
          <input
            type="text"
            placeholder = "Add Map URL"
            value={this.state.mapUrl}
            onChange= {this.handleChangeFor('mapUrl')}
          />
          <input
            type="text"
            placeholder = "Add Timeline URL"
            value={this.state.timelineUrl}
            onChange= {this.handleChangeFor('timelineUrl')}
          />
          <input
            type="text"
            placeholder = "Add Picture URL"
            value={this.state.galleryUrl}
            onChange= {this.handleChangeFor('galleryUrl')}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.response}</p>
        </div>
    );
  }
}

export default AddInfo;
