import React, { Component } from 'react';
import './App.css';
import AddInfo from '../AddInfo/AddInfo'
import Maps from '../Maps/Maps'
import Lesson from '../Lesson/Lesson'
import Study from '../Study/Study'
import Timeline from '../Timeline/Timeline'
import Calendar from '../Calendar/Calendar'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { HashRouter as Router, Link, Route, Redirect } from 'react-router-dom'

class App extends Component {

  state = {
    email: "",
    password: "",
    auth: false,
    validated: false
  };

  componentDidMount() {
    this.getAllPeople()
      .then(res => this.setState({ response: res }))
      .catch(err => console.log(err));
  }

  getAllPeople = async () => {
    const response = await fetch('/api/people');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getOnePerson = async () => {
    const response = await fetch('/api/onePerson')
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  }

  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  checkUser = async event => {
    const objectToSend = {
      user: this.state.email,
      password: this.state.password
    }
      event.preventDefault();
      const response = await fetch('/api/checkUser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ postUser: objectToSend }),
      });
      const body = await response.text();
      console.log(typeof body)
      if(body === 'true') {
        this.setState({
          auth: true,
          tryAgain: false
        });
        window.location = '#/peopleOfPromise';
      } else {
        this.setState({
          tryAgain: true
        });
      }
      }


render() {
    return (
      <Router>
      <div className="App">
        <header className={this.state.auth ? "App-header" : 'hidden' }>
        <Link className = "link-options" to = '/peopleOfPromise'>People of Promise</Link>
        <Link className = "link-options" to = '/lesson'>Lesson</Link>
        <Link className = "link-options" to = '/study'>Study</Link>
        <Link className = "link-options" to = '/maps'>Maps</Link>
        <strong className = "link-options" >People Of Promise</strong>
        <Link className = "link-options" to = '/timeline'>Timeline</Link>
        <Link className = "link-options" to = '/gallery'>Gallery</Link>
        <Link className = "link-options" to = '/youth'>Youth</Link>
        <Link className = "link-options" to = '/addInfo'>Admin Page</Link>
        </header>
        <div className= {this.state.auth ? "hidden" : 'Login'}>
      <h1>Website Coming Soon...</h1>
        <Form 
        onSubmit={this.checkUser}>
          <Form.Group controlId="email" bssize="large">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="text"
              value={this.state.email}
              onChange={this.handleChange}
            />
            <Form.Control.Feedback type="invalid">
              User is incorrect
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="password" bssize="large">
            <Form.Label>Password</Form.Label>
            <Form.Control
              value={this.state.password}
              onChange={this.handleChange}
              type="password"
            />
            <Form.Control.Feedback type="invalid">
              Password is incorrect
            </Form.Control.Feedback>
          </Form.Group>
          <Button
            block
            bsSize="large"
            disabled={!this.validateForm()}
            type="submit"
          >
            Login
          </Button>

        </Form>
      </div>
        <Route 
         path = '/addInfo' render = {() => (this.state.auth ? <AddInfo /> : <Redirect to = '/'/>)}
        />
        <Route 
        path = '/maps' render = {() => (this.state.auth ? <Maps /> : <Redirect to = '/'/>)}
        />
        <Route 
        path = '/lesson' render = {() => (this.state.auth ? <Lesson /> : <Redirect to = '/'/>)}
        />
        <Route 
        path = '/study' render = {() => (this.state.auth ? <Study /> : <Redirect to = '/'/>)}
        />
        <Route 
        path = '/timeline' render = {() => (this.state.auth ? <Timeline/> : <Redirect to = '/'/>)}
        />
        <Route 
        path = '/peopleOfPromise' render = {() => (this.state.auth ? <Calendar /> : <Redirect to = '/'/>)}
        />
      </div>
      </Router>
    );
  }
}

export default App;
