import React, { Component } from 'react';
import './AddPerson.css';

class AddPerson extends Component {



    render() {

    return (
        <div>
        <form onSubmit={this.props.handleSubmit}>
          <p>
            <strong>Create New Biblical Hero:</strong>
          </p>
          <input
            type="text"
            placeholder = "Name"
            value={this.props.post.name}
            onChange= {this.props.handleChangeFor('name')}
          />
          <input
            type="text"
            placeholder = "Nickname"
            value={this.props.post.nickname}
            onChange= {this.props.handleChangeFor('nickname')}
          />
          <input
            type="text"
            placeholder = "Lesson"
            value={this.props.post.lesson}
            onChange= {this.props.handleChangeFor('lesson')}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.props.responseToPost}</p>
        </div>
    );
  }
}

export default Information;
