import React, { Component } from 'react';
import './AddInfo.css';
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

class AddInfo extends Component {

  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false
    };
  }

  state = {
    lesson: '',
    question: '',
    mapUrl: '',
    timelineUrl: '',
    galleryUrl: '',
    dropdownOpen: true,
    books: {
      id: [], 
      name: []
    }
  }

getVerse = async () => {

  const response = await fetch('https://api.scripture.api.bible/v1/bibles/f72b840c855f362c-04/books', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'api-key': '230adc9049bc15384c218ba474b48eca'
    },
  })
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  this.setState({lesson: body})
  console.log(this.state.lesson)
  return body
}

getBooksAndNameAndSave = async () => {
  const response = await fetch('https://api.scripture.api.bible/v1/bibles/f72b840c855f362c-04/books', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'api-key': '230adc9049bc15384c218ba474b48eca'
    },
  })
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  const ids = [];
  const names = [];
  for(let book of body.data) {
      ids.push(book.id)
      names.push(book.name)
  }
  this.setState({books: ids})
  this.setState({books: names})

  this.setState({lesson: body})
  console.log(this.state.lesson)
  return body
}

handleChangeFor = (property) => {
  return (event) => {
      this.setState({
              [property]: event.target.value,
  })
}
}

toggle = () => {
  this.setState(prevState => ({
    dropdownOpen: !prevState.dropdownOpen
  }));
}

// postInfo = (name) => {
//   const response = await fetch('/api/addPerson', {
//     method: 'POST',
//     headers: {
//       'Content-Type': 'application/json',
//     },
//     body: JSON.stringify({ postPerson: this.state.postPerson }),
//   });
//   const body = await response.text();
// }

render() {

    return (
        <div>
        <form onSubmit={this.props.handleSubmit}>
          <p>
            <strong>Add Information for {this.props.name}</strong>
          </p>
          <p>Find New Passage</p>
          <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
          <DropdownToggle caret>
          Dropdown
        </DropdownToggle>
        <DropdownMenu>
          <DropdownItem>Foo Action</DropdownItem>
        </DropdownMenu>
          </Dropdown>
          <button onClick= {this.getVerse}>Submit</button>
          <input
            type="text"
            placeholder = "Add Question"
            value={this.state.question}
            onChange= {this.handleChangeFor('question')}
          />
          <input
            type="text"
            placeholder = "Add Map URL"
            value={this.state.mapUrl}
            onChange= {this.handleChangeFor('mapUrl')}
          />
          <input
            type="text"
            placeholder = "Add Timeline URL"
            value={this.state.timelineUrl}
            onChange= {this.handleChangeFor('timelineUrl')}
          />
          <input
            type="text"
            placeholder = "Add Picture URL"
            value={this.state.galleryUrl}
            onChange= {this.handleChangeFor('galleryUrl')}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.response}</p>
        </div>
    );
  }
}

export default AddInfo;
