import React, { Component } from 'react';
import './AddPerson.css';
import { Redirect } from 'react-router-dom'

class AddPerson extends Component {
  state = {
    redirect: false,
    postPerson: {
      biblical_name: '',
      nickname: ''
    },
    responseToPost: ''
  }

  setRedirect = () => {
    this.setState({
      redirect: true
    })
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/addInfo' />
    }
  }

  handleChangeFor = (property) => {
    return (event) => {
        this.setState({
            postPerson: {
                ...this.state.postPerson,
                [property]: event.target.value,
            }
    })
}
}

handleSubmitPerson = async event => {
  event.preventDefault();
  const response = await fetch('/api/addPerson', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ postPerson: this.state.postPerson }),
  });
  const body = await response.text();
  this.setState({ responseToPost: body });
  this.setRedirect();
}

handleChangeFor = (property) => {
  return (event) => {
      this.setState({
          postPerson: {
              ...this.state.postPerson,
              [property]: event.target.value,
          }
  })
}
}

  render() {

    return (
        <div>
          {this.renderRedirect()}
        <form onSubmit={this.state.handleSubmitPerson}>
          <p>
            <strong>Create New Biblical Hero:</strong>
          </p>
          <input
            type="text"
            placeholder = "Name"
            value={this.state.postPerson.name}
            onChange= {this.state.handleChangeFor('biblical_name')}
          />
          <input
            type="text"
            placeholder = "Nickname"
            value={this.state.postPerson.nickname}
            onChange= {this.state.handleChangeFor('nickname')}
          />
          <button type="submit">Continue</button>
        </form>
        <p>{this.state.responseToPost}</p>
        </div>
    );
  }
}

export default AddPerson;
