import React, { Component } from 'react';
import './App.css';
import AddInfo from '../AddInfo/AddInfo'
import Maps from '../Maps/Maps'
import Lesson from '../Lesson/Lesson'
import Study from '../Study/Study'
import Timeline from '../Timeline/Timeline'
import Calendar from '../Calendar/Calendar'
import { HashRouter as Router, Link, Route, Redirect } from 'react-router-dom'

class App extends Component {

  state = {
    postPerson: {
    biblical_name: '',
    nickname: ''
    }
  };

  componentDidMount() {
    this.getAllPeople()
      .then(res => this.setState({ response: res }))
      .catch(err => console.log(err));
  }

  getAllPeople = async () => {
    const response = await fetch('/api/people');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getOnePerson = async () => {
    const response = await fetch('/api/onePerson')
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  }

  handleChangeFor = (property) => {
    return (event) => {
        this.setState({
            postPerson: {
                ...this.state.postPerson,
                [property]: event.target.value,
            }
    })
  }
}

render() {
    return (
      <Router>
      <div className="App">
        <header className="App-header">
        <Link className = "link-options" to = '/peopleOfPromise'>People of Promise</Link>
        <Link className = "link-options" to = '/lesson'>Lesson</Link>
        <Link className = "link-options" to = '/study'>Study</Link>
        <Link className = "link-options" to = '/maps'>Maps</Link>
        <strong className = "link-options" >People Of Promise</strong>
        <Link className = "link-options" to = '/timeline'>Timeline</Link>
        <Link className = "link-options" to = '/gallery'>Gallery</Link>
        <Link className = "link-options" to = '/youth'>Youth</Link>
        <Link className = "link-options" to = '/addInfo'>Admin Page</Link>
        </header>
        <Route 
        path = '/addInfo' render = {() => (<AddInfo postPerson = {this.state.postPerson} handleChangeFor = {this.handleChangeFor} name = {this.state.postPerson.biblical_name}/>)}
        />
        <Route 
        path = '/maps' render = {() => (<Maps name = {this.state.postPerson.biblical_name}/>)}
        />
        <Route 
        path = '/lesson' render = {() => (<Lesson name = {this.state.postPerson.biblical_name}/>)}
        />
        <Route 
        path = '/study' render = {() => (<Study name = {this.state.postPerson.biblical_name}/>)}
        />
        <Route 
        path = '/timeline' render = {() => (<Timeline name = {this.state.postPerson.biblical_name}/>)}
        />
        <Route 
        path = '/peopleOfPromise' render = {() => (<Calendar name = {this.state.postPerson.biblical_name}/>)}
        />
      </div>
      </Router>
    );
  }
}

export default App;
