import React, { Component } from 'react';
import './AddInfo.css';

class AddInfo extends Component {

  state = {
    postToInfo: {
      lesson: '',

    }
  }

getVerse = async () => {

  const response = await fetch('https://api.scripture.api.bible/v1/bibles/f72b840c855f362c-04/passages/GEN.12.1-GEN.12.9?content-type=text', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'api-key': '230adc9049bc15384c218ba474b48eca'
    },
  })
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  this.setState({lesson: body.data.content})
  console.log(this.state.response)
  return body
}


    render() {

    return (
        <div>
        <form onSubmit={this.props.handleSubmit}>
          <p>
            <strong>Create New Biblical Hero:</strong>
          </p>
          <input
            type="text"
            placeholder = "Lesson"
            value={this.props.post.lesson}
            onChange= {this.props.handleChangeFor('lesson')}
          />
          <button onClick= {this.getVerse}>Submit</button>
          <input
            type="text"
            placeholder = "Add Question"
            value={this.props.post.lesson}
            onChange= {this.props.handleChangeFor('lesson')}
          />
          <input
            type="text"
            placeholder = "Questions"
            value={this.props.post.lesson}
            onChange= {this.props.handleChangeFor('lesson')}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.response}</p>
        </div>
    );
  }
}

export default AddInfo;
