import React, { Component } from 'react';
import './AddPerson.css';
import { Redirect } from 'react-router-dom'

class AddPerson extends Component {

  state = {
    responseToPost: ''
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/addInfo' />
    }
  }

handleSubmitPerson = async event => {
  event.preventDefault();
  const response = await fetch('/api/addPerson', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ postPerson: this.props.postPerson }),
  });
  const body = await response.text();
  this.setState({ responseToPost: body });
}
  render() {

    return (
        <div>
        <form onSubmit={this.handleSubmitPerson}>
          <p>
            <strong>Create New Biblical Hero:</strong>
          </p>
          <div>
          <input
            type="text"
            className = 'input-fields'
            placeholder = "Name"
            value={this.props.postPerson.biblical_name}
            onChange= {this.props.handleChangeFor('biblical_name')}
          />
          <input
            type="text"
            className = 'input-fields'
            placeholder = "Nickname"
            value={this.props.postPerson.nickname}
            onChange= {this.props.handleChangeFor('nickname')}
          />
          </div>
          <button type="submit">Continue</button>
        </form>
        <p>{this.state.responseToPost}</p>
        </div>
    );
  }
}

export default AddPerson;
