import React, { Component } from 'react';
import './AddPerson.css';
import { Redirect } from 'react-router-dom'

class AddPerson extends Component {
  state = {
    redirect: false
  }

  setRedirect = () => {
    this.setState({
      redirect: true
    })
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/addInfo' />
    }
  }

  handleChangeFor = (property) => {
    return (event) => {
        this.setState({
            postPerson: {
                ...this.state.postPerson,
                [property]: event.target.value,
            }
    })
}
}

  render() {

    return (
        <div>
        <form onSubmit={this.props.handleSubmit}>
          <p>
            <strong>Create New Biblical Hero:</strong>
          </p>
          <input
            type="text"
            placeholder = "Name"
            value={this.props.post.name}
            onChange= {this.props.handleChangeFor('biblical_name')}
          />
          <input
            type="text"
            placeholder = "Nickname"
            value={this.props.post.nickname}
            onChange= {this.props.handleChangeFor('nickname')}
          />
          <button type="submit">Continue</button>
        </form>
        <p>{this.props.responseToPost}</p>
        {this.renderRedirect()}
        </div>
    );
  }
}

export default AddPerson;
